#pragma config(Sensor, S1,     SE,             sensorEV3_Color)
#pragma config(Sensor, S2,     SM,             sensorEV3_Color)
#pragma config(Sensor, S3,     SD,             sensorEV3_Color)
#pragma config(Motor,  motorA,          ME,            tmotorEV3_Large, PIDControl, encoder)
#pragma config(Motor,  motorB,          MD,            tmotorEV3_Large, PIDControl, encoder)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

task main()
{
//	int ve, vd;
	clearTimer(T1);
	int vo=40, erro, erro_ant, n_sensores, kp=4, flag=0, contador=0, t=0;
	while(t<770){
		t=time100[T1];
		n_sensores=0;
		erro_ant=erro;
		erro=0;


		if(SensorValue[SE]>40){
				n_sensores+=2;
				erro+=18;}
		if(SensorValue[SM]>40){
				n_sensores++;

				}
		if(SensorValue[SD]>40){
				n_sensores+=2;
				erro-=18;}

		if((erro_ant == 9 || erro_ant==0 || erro_ant==6) && erro == -9 )
		{
			erro=erro_ant;
		}
		if((erro_ant == -9 || erro_ant==0 || erro_ant==-6) && erro == 9)
		{
			erro=erro_ant;
		}

		if(erro==9)
			flag=1;
		if(erro==-9)
			flag=-1;
		if(flag==1||flag==-1)
			contador++;
		if(contador>75){
			flag=0;
			contador=0;}

		if(n_sensores>0)
			erro=erro/n_sensores;
		else
			erro=erro_ant;
		if(erro==0)
			erro=-3*flag;
	if(erro>0){
	motor[ME]=vo-kp*erro;
	motor[MD]=vo;}
	else{
	motor[ME]=vo;
	motor[MD]=vo+kp*erro;}

	}
}
